import './animal.css';
import { useNavigate, useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { getPetById, updatePet } from '../../../../services/api';

export default function EditPet() {
    const { id } = useParams();
    const navigate = useNavigate();

    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [pet, setPet] = useState({
        name: "",
        birthDate: "",
        type: "",
        ownerId: ""
    });

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await getPetById(id);
            // Extraire les données du pet de la réponse
            // La réponse semble contenir un objet avec pet et visits
            const { pet: petData } = response.data;
            
            // Format de la date pour l'input date
            const formattedDate = petData.birthDate ? new Date(petData.birthDate).toISOString().split('T')[0] : '';
            
            setPet({
                name: petData.name,
                birthDate: formattedDate,
                type: petData.type,
                ownerId: petData.ownerId
            });
        } catch (error) {
            console.error("Error while fetching data:", error);
            setError("Failed to load pet data");
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setPet({ ...pet, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!pet.name.trim() || !pet.birthDate.trim() || !pet.type.trim()) {
            setError("All fields are required");
            return;
        }

        setError(null);
        setLoading(true);

        try {
            const response = await updatePet(id, pet);
            if (response.status === 200) {
                console.log("Pet updated successfully.");
                navigate(`/pets/${id}`);
            }
        } catch (err) {
            console.error("Error updating pet:", err);
            setError("Failed to update pet. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container">
            <h1>Edit Pet</h1>
            {loading ? (
                <div className="mt-4 d-flex justify-content-center">
                    <div className="spinner-border" role="status">
                        <span className="sr-only">Loading...</span>
                    </div>
                </div>
            ) : (
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="input-name" className="form-label">Name:</label>
                        <input
                            type="text"
                            id="input-name"
                            name="name"
                            value={pet.name}
                            onChange={handleChange}
                            className="form-control col-8 ml-0 text-hover-costem"
                            placeholder="Pet Name"
                        />
                    </div>
                    
                    <div className="mb-3">
                        <label htmlFor="input-birthday" className="form-label">Birth Date:</label>
                        <input
                            type="date"
                            id="input-birthday"
                            name="birthDate"
                            value={pet.birthDate}
                            onChange={handleChange}
                            className="form-control col-8 ml-0 text-hover-costem"
                        />
                    </div>
                    
                    <div className="mb-3">
                        <label htmlFor="input-type" className="form-label">Type:</label>
                        <select
                            className="form-select form-control"
                            id="input-type"
                            name="type"
                            value={pet.type}
                            onChange={handleChange}
                        >
                            <option value="">Select a type</option>
                            <option value="dog">Dog</option>
                            <option value="cat">Cat</option>
                            <option value="bird">Bird</option>
                            <option value="reptile">Reptile</option>
                            <option value="hamster">Hamster</option>
                            <option value="other">Other</option>
                        </select>
                    </div>
                    
                    {error && <div className="alert alert-danger">{error}</div>}
                    
                    <button type="submit" className="btn-fowner" disabled={loading}>
                        {loading ? "Updating..." : "Update Pet"}
                    </button>
                </form>
            )}
        </div>
    );
}
