import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import { getOwnerPets } from "../../../../services/api";


function Info() {
    const { id } = useParams()
    const [owner, setOwner] = useState({});
    const [ownerPets, setOwnerPets] = useState([]);
    const [loading, setLoading] = useState(true);
    const [loadingPets, setLoadingPets] = useState(true);

    useEffect(() => {
        fetchData();
        fetchOwnerPets();
      }, []);
    
    const fetchData = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/api/owners/${id}`);
          setOwner(response.data);
        } catch (error) {
          console.log("Error while fetching data", error);
        } finally {
            setLoading(false);
        }
    };
    
    const fetchOwnerPets = async () => {
        try {
          const response = await getOwnerPets(id);
          setOwnerPets(response.data);
        } catch (error) {
          console.error("Error while fetching data", error);
        } finally {
            setLoadingPets(false);
        }
    };


    return (
            <div className="container mt-4">
                <div className="row mb-4">
                    <div className="col">
                        <h1 className="section-title">Owner Information</h1>
                        {loading ? 
                            <div className="mt-4 d-flex justify-content-center">
                                <div className="spinner-border" role="status">
                                    <span className="sr-only">Loading...</span>
                                </div>
                            </div>
                        :
                        <>
                            <div className="row mb-3">
                                <div className="col-md-2 fw-bold">Name</div>
                                <div className="col-md-10">{owner.First_Name} {owner.Last_Name}</div>
                            </div>
                            <div className="row mb-3">
                                <div className="col-md-2 fw-bold">Address</div>
                                <div className="col-md-10">{owner.Adress}.</div>
                            </div>
                            <div className="row mb-3">
                                <div className="col-md-2 fw-bold">City</div>
                                <div className="col-md-10">{owner.City}</div>
                            </div>
                            <div className="row mb-3">
                                <div className="col-md-2 fw-bold">Telephone</div>
                                <div className="col-md-10">{owner.Telephone}</div>
                            </div>
                            <div className="mt-3">
                                <Link to="/UpdateOwner" className="btn-fowner">Edit Owner</Link>
                                <Link to="/AddPet" className="btn-fowner">Add New Pet</Link>
                            </div>
                        </>
                        }
                    </div>
                </div>
    
                <div className="row">
                    <div className="col">
                        <h2 className="section-title">Pets and Visits</h2>
                        {loadingPets ? 
                            <div className="mt-4 d-flex justify-content-center">
                                <div className="spinner-border" role="status">
                                    <span className="sr-only">Loading...</span>
                                </div>
                            </div>
                        :
                        <>
                            {ownerPets.map(pet => 
                                <div key={pet._id} className="card pet-card mb-4">
                                    <div className="card-body">
                                        <h5 className="card-title">{pet.name}</h5>
            
                                        <div className="row mb-2">
                                            <div className="col-md-2 fw-bold">Birth Date</div>
                                            <div className="col-md-4">{pet.birthDate}</div>
                                            <div className="col-md-2 fw-bold">Visit Date</div>
                                            <div className="col-md-4">2025-03-09</div>
                                        </div>
                                        <div className="row mb-3">
                                            <div className="col-md-2 fw-bold">Description</div>
                                            <div className="col-md-10">Analyse complete</div>
                                        </div>
                                        <div className="row">
                                            <div className="col-md-2 fw-bold">Type</div>
                                            <div className="col-md-4">{pet.type}</div>
                                            <div className="col-md-6 text-end">
                                                <Link to="/AddVisit" className="btn-fowner">Add Visit</Link>
                                                <Link to="/UpdatePet" className="btn-fowner">Edit Pet</Link>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            )}
                        </>
                        }
                    </div>
                </div>
            </div>
    );
}
export default Info
