// src/components/pages/visits/CreateVisit.jsx
import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
// Adjust the import path if your api.js location is different
import { createVisit } from '../../../../services/api';

function CreateVisit() {
  const { petId } = useParams(); // Get petId from the URL - This should already be correct
  const navigate = useNavigate();
  const [visitDate, setVisitDate] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(''); // Keep success state if you want feedback

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true); // Start loading

    if (!visitDate || !description) {
      setError('Both date and description are required.');
      setLoading(false); // Stop loading
      return;
    }

    const visitData = {
      date: visitDate,
      description: description,
    };

    try {
      const response = await createVisit(petId, visitData);

      if (response.status === 201) {
        setSuccess('Visit added successfully!');
        setTimeout(() => {
           navigate(-1);
        }, 1500);
      } else {
          setError(`Visit creation returned status ${response.status}`);
      }
    } catch (err) {
      console.error('Error creating visit:', err);
      const errorMsg = err.response?.data?.message || err.message || 'An error occurred while adding the visit.';
      setError(errorMsg);
    } finally {
      setLoading(false); 
    }
  };

  return (
    <div className="container mt-4">
      <h2>Add Visit</h2>
      <form onSubmit={handleSubmit}>
        {error && <div className="alert alert-danger">{error}</div>}
        {success && <div className="alert alert-success">{success}</div>}

        <div className="mb-3">
          <label htmlFor="visitDate" className="form-label">Date</label>
          <input
            type="date"
            className="form-control"
            id="visitDate"
            value={visitDate}
            onChange={(e) => setVisitDate(e.target.value)}
            required
            disabled={loading}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="description" className="form-label">Description</label>
          <textarea
            className="form-control"
            id="description"
            rows="3"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            disabled={loading}
          ></textarea>
        </div>
        <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Adding Visit...' : 'Add Visit'}
        </button>
         <button type="button" className="btn btn-secondary ms-2" onClick={() => navigate(-1)} disabled={loading}>Cancel</button>
      </form>
    </div>
  );
}

export default CreateVisit;
