import './owner.css';
import Navbar from "./Navbar";
import Footer from "./Footer";
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { ownerService } from '../services/api';

export default function Owner() {
    const [lastName, setLastName] = useState('');
    const [owners, setOwners] = useState([]);
    const [searchPerformed, setSearchPerformed] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleSearch = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);
        
        try {
            let response;
            if (lastName.trim() === '') {
                response = await ownerService.getAll();
            } else {
                response = await ownerService.search(lastName);
            }
            setOwners(response.data);
            setSearchPerformed(true);
        } catch (err) {
            console.error('Error searching owners:', err);
            setError('Failed to search owners. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <>
        <Navbar/>
            <div className='general'>
                <div className='container'>
                    <br /><br /><br />
                    <h1> Find Owner </h1>
                    <br /><br /><br />
                    <form onSubmit={handleSearch}>
                        <label> Last Name :</label>
                        <input 
                            type="text" 
                            id="input-name" 
                            className="form-control col-8 ml-0 text-hover-costem" 
                            placeholder="Last Name" 
                            value={lastName}
                            onChange={(e) => setLastName(e.target.value)}
                        />

                        <button type="submit" className="btn-fowner">
                            Find Owner
                        </button>
                    </form>
                        <br /><br />
                        
                    <Link to="/AddOwner" className="btn-fowner">
                        Add Owner
                    </Link>
                </div>
            </div>

            {loading && <div className="text-center my-4">Loading...</div>}
            {error && <div className="alert alert-danger my-4">{error}</div>}

            {searchPerformed && owners.length > 0 && (
                <div className="container mt-4">
                    <h1 className="mb-4">Owners</h1>
                    <br />
                    <div className="table-responsive">
                        <table className="table table-striped table-hover">
                            <thead className="table-dark">
                                <tr>
                                    <th>Name</th>
                                    <th>Address</th>
                                    <th>City</th>
                                    <th>Telephone</th>
                                    <th>Pets</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {owners.map(owner => (
                                    <tr key={owner._id}>
                                        <td>{owner.firstName} {owner.lastName}</td>
                                        <td>{owner.address}</td>
                                        <td>{owner.city}</td>
                                        <td>{owner.telephone}</td>
                                        <td>
                                            {/* This would ideally be populated in the backend */}
                                            {owner.pets?.map(pet => pet.name).join(', ') || 'No pets'}
                                        </td>
                                        <td>
                                            <Link to={`/OwnerInfo/${owner._id}`} className="btn btn-sm btn-primary">
                                                View
                                            </Link>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            )}

            {searchPerformed && owners.length === 0 && (
                <div className="container mt-4">
                    <div className="alert alert-info">
                        No owners found. Please try a different search or add a new owner.
                    </div>
                </div>
            )}

            <Footer/>
        </>
    );
}
