// src/components/pages/visits/CreateVisit.jsx
import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { createVisit } from '../../../../services/api'; // Adjust the import path as needed

function CreateVisit() {
  const { petId } = useParams(); // Get petId from the URL
  const navigate = useNavigate();
  const [visitDate, setVisitDate] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');
    setSuccess('');

    if (!visitDate || !description) {
      setError('Both date and description are required.');
      return;
    }

    const visitData = {
      petId: petId, // Pass the petId obtained from URL params
      date: visitDate,
      description: description,
    };

    try {
      const response = await createVisit(visitData); // Use createVisit from api.js
      if (response.status === 201) {
        setSuccess('Visit added successfully!');
        // Optionally navigate back to the owner/pet info page after a delay
        setTimeout(() => {
          // You might need ownerId here depending on your desired navigation
          // If you don't have ownerId readily available, navigate somewhere else or just show success
           navigate(-1); // Go back to the previous page
        }, 1500);
      } else {
          setError('Failed to add visit. Status: ' + response.status);
      }
    } catch (err) {
      console.error('Error creating visit:', err);
      setError(err.message || 'An error occurred while adding the visit.');
    }
  };

  return (
    <div className="container mt-4">
      <h2>Add Visit</h2>
      <form onSubmit={handleSubmit}>
        {error && <div className="alert alert-danger">{error}</div>}
        {success && <div className="alert alert-success">{success}</div>}

        {/* Display Pet ID if needed */}
        {/* <p>Adding visit for Pet ID: {petId}</p> */}

        <div className="mb-3">
          <label htmlFor="visitDate" className="form-label">Date</label>
          <input
            type="date"
            className="form-control"
            id="visitDate"
            value={visitDate}
            onChange={(e) => setVisitDate(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="description" className="form-label">Description</label>
          <textarea
            className="form-control"
            id="description"
            rows="3"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>
        </div>
        <button type="submit" className="btn-fowner">Add Visit</button>
         <button type="button" className="btn btn-secondary ms-2" onClick={() => navigate(-1)}>Cancel</button>
      </form>
    </div>
  );
}

export default CreateVisit;
