import './animal.css';
import { useNavigate, useParams } from 'react-router-dom';
import { useState } from 'react';
import { createVisit } from '../../../../services/api';


export default function CreateVisit() {
    const { id, visitId } = useParams();
    const navigate = useNavigate();

    const [error, setError] = useState(null)
    const [loading, setLoading] = useState(false)
    const [visit, setVisit] = useState({
        date: "",
        description: "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setVisit({...visit, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        
        if (!visit.date.trim() || !visit.description.trim()) {
            setError("All fields are required");
            return;
        }
        
        setError(null);
        
        try {
            setLoading(true);
            const response = await createPet(id, visit)
            if (response.status === 201) {
                console.log("visit created successfully.")
                navigate(`/owners/${id}`);
            }
        } catch (err) {
            if(err.status === 422) {
                setError(err.response.data.message);
            } else {
                console.error("Error creating visit:", err);
            }
        } finally {
            setLoading(false);
        }
    };


    return (
        <div className="container">
            <h1>Add  New  Visit</h1>
            <form onSubmit={handleSubmit}>
                <input type="date" id="input-name" name='date' value={visit.date} onChange={handleChange} class="form-control col-8 ml-0 text-hover-costem" />
                
                <label htmlFor=""> Description : </label>
                <br /> 
                <textarea
                    className="form-control"
                    name='description'
                    rows="3"
                    value={visit.description}
                    onChange={handleChange}
                    required
                    disabled={loading}
                ></textarea>
                
                {error && (
                    <div className="alert alert-danger" role="alert">
                        {error}
                    </div>
                )}

                <button disabled={loading} type="submit" className="btn-fowner">
                    {loading ? "Adding..." : "Add Visit"}
                </button>
                
            </form>
        </div>
    );
}
