import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";


function Info() {
    const { id } = useParams()
    const [owner, setOwner] = useState({});
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchData();
        getOwnerPets();
      }, []);
    
    const fetchData = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/api/owners/${id}`);
          setOwner(response.data);
        } catch (error) {
          console.log("Error while fetching data", error);
        } finally {
            setLoading(false);
        }
    };
    
    const getOwnerPets = async () => {
        try {
          const response = await getOwnerPets(id);
          setOwner(response.data);
        } catch (error) {
          console.log("Error while fetching data", error);
        } finally {
            setLoading(false);
        }
    };


    return (
        <>
        {
            loading ? 
                <div className="mt-4 d-flex justify-content-center">
                    <div className="spinner-border" role="status">
                        <span className="sr-only">Loading...</span>
                    </div>
                </div>
            :
            <div class="container mt-4">
                <div class="row mb-4">
                    <div class="col">
                        <h1 class="section-title">Owner Information</h1>
                        <div class="row mb-3">
                            <div class="col-md-2 fw-bold">Name</div>
                            <div class="col-md-10">{owner.First_Name} {owner.Last_Name}</div>
                        </div>
                        <div class="row mb-3">
                            <div class="col-md-2 fw-bold">Address</div>
                            <div class="col-md-10">{owner.Adress}.</div>
                        </div>
                        <div class="row mb-3">
                            <div class="col-md-2 fw-bold">City</div>
                            <div class="col-md-10">{owner.City}</div>
                        </div>
                        <div class="row mb-3">
                            <div class="col-md-2 fw-bold">Telephone</div>
                            <div class="col-md-10">{owner.Telephone}</div>
                        </div>
                        <div class="mt-3">
                            <Link to="/UpdateOwner" className="btn-fowner">Edit Owner</Link>
                            <Link to="/AddPet" className="btn-fowner">Add New Pet</Link>
                        </div>
                    </div>
                </div>
    
                <div class="row">
                    <div class="col">
                        <h2 class="section-title">Pets and Visits</h2>
    
                        <div class="card pet-card mb-4">
                            <div class="card-body">
                                <h5 class="card-title">Lucky</h5>
    
                                <div class="row mb-2">
                                    <div class="col-md-2 fw-bold">Birth Date</div>
                                    <div class="col-md-4">2010-06-24</div>
                                    <div class="col-md-2 fw-bold">Visit Date</div>
                                    <div class="col-md-4">2025-03-09</div>
                                </div>
                                <div class="row mb-3">
                                    <div class="col-md-2 fw-bold">Description</div>
                                    <div class="col-md-10">Analyse complete</div>
                                </div>
                                <div class="row">
                                    <div class="col-md-2 fw-bold">Type</div>
                                    <div class="col-md-4">dog</div>
                                    <div class="col-md-6 text-end">
                                    <Link to="/AddVisit" className="btn-fowner">
                                            Add Visit
                                        </Link>
                                        <Link to="/UpdatePet" className="btn-fowner">
                                            Edit Pet
                                        </Link>
                                    </div>
                                </div>
                            </div>
                        </div>
    
    
                        
                    </div>
                </div>
            </div>
        }
        </>
    );
}
export default Info
