import './animal.css';
import { Link, useNavigation } from 'react-router-dom';
import { useState } from 'react';
import { createPet } from '../../../../services/api';


export default function CreatePet() {
        const navigate = useNavigation();
        const [error, setError] = useState(null)
        const [loading, setLoading] = useState(false)
        const [pet, setPet] = useState({
            name: "",
            birthDate: "",
            type: "",
            ownerId: ""
        });
    
        const handleChange = (e) => {
            // const name = e.target.name
            // const value = e.target.value
            const { name, value } = e.target;
            setPet({...pet, [name]: value });
        };
    
        const handleSubmit = async (e) => {
            e.preventDefault();
    
            
            if (!pet.name.trim() || !pet.birthDate.trim() || !pet.type.trim() || !pet.ownerId.trim()) {
                setError("All fields are required");
                return;
            }
            
            setError(null);
            
            try {
                setLoading(true);
                const response = await createPet(pet)
                if (response.status === 201) {
                    console.log("pet created successfully.")
                    navigate("/owners");
                }
            } catch (err) {
                if(err.status === 422) {
                    setError(err.response.data.message);
                } else {
                   console.error("Error creating pet:", err);
                }
            } finally {
                setLoading(false);
            }
        };
    return (
        <>
            <div>

                <h1>
                Add  New  Pet
                </h1>

                <div className="container">
                    <label> Owner</label>
                    <br />
                    <label> Carlos Estaban</label>
                    <br />
                    <input type="text" id="input-name" name='name' value={pet.name} onChange={handleChange} class="form-control col-8 ml-0 text-hover-costem" placeholder="Pet Name" aria-label="Username" aria-describedby="addon-wrapping" />
                    <label htmlFor=""> Birth day : </label>
                    <br />
                    <input type="date" id="input-name" name='birthDate' value={pet.birthDate}
                                onChange={handleChange} class="form-control col-8 ml-0 text-hover-costem" placeholder=" " aria-label="Username" aria-describedby="addon-wrapping" />
                   
                    <label htmlFor=""> Type : </label>
                    <br /> 
                    <select class="form-select form-control" id="input-name" name='type' value={pet.type} onChange={handleChange}  multiple aria-label="Multiple select example">
                        <option selected>Open this select menu</option>
                        <option value="1">One</option>
                        <option value="2">Two</option>
                        <option value="3">Three</option>
                    </select>
                    <Link to="/infoPet" className="btn-fowner">
                        Add Pet
                    </Link>
                </div>
            </div>
        </>
    );
}
