import './owners.css';
import Navbar from "../../Navbar";
import Footer from "../../Footer";
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';


export default function Owners() {
    const[owners, setOwners] = useState([])
    useEffect(() => {
        // console.log("aklaskldjklasjdklasjdklasjd")
        fetchData()
    }, []);
    
    const fetchData = async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/owners")
            setOwners(response.data)
        } catch (error) {
            console.log("Error while fetching data", error)
        }
    };
    
    
    return (
        <>
        <Navbar/>
            <div className='general'>
                <div className='container '>
                    <br /><br /><br />
                    <h1> Find Owner </h1>
                    <br /><br /><br />
                    <label> Last Name :</label>
                    <input type="text" id="input-name" className="form-control col-8 ml-0 text-hover-costem" placeholder="Last Name" aria-label="Username" aria-describedby="addon-wrapping" />

                    <Link to="/FindOwner" className="btn-fowner">
                        Find Owner
                    </Link>
                        <br /><br />
                        
                    <Link to="/AddOwner" className="btn-fowner">
                        Add Owner
                    </Link>
                </div>

            </div>

            <>
        
        <div className="container mt-4">
            <h1 className="mb-4">Owners</h1>
            <br />
            <div className="table-responsive ">
                <table className="table table-striped table-hover">
                    <thead className="table-dark">
                        <tr>
                            <th>Name</th>
                            <th>Address</th>
                            <th>City</th>
                            <th>Telephone</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {owners.map(owner => 
                            <tr key={owner._id}>
                                <td>
                                    <Link to="/owner/:id">{owner.First_Name} {owner.Last_Name}</Link>
                                </td>
                                <td>{owner.Adress}</td>
                                <td>{owner.City}</td>
                                <td>{owner.Telephone}</td>
                                <td></td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
            </>
            <Footer/>
        </>
    );
}
